	&lt;/tr&gt;
	&lt;tr&gt;
		&lt;th&gt;resume&lt;/th&gt;
		&lt;td&gt;&lt;input name="resume" value="&lt;?php echo $this-&gt;oArticle-&gt;resume ?&gt;" /&gt;&lt;?php if($this-&gt;tMessage and isset($this-&gt;tMessage['resume'])): echo implode(',',$this-&gt;tMessage['resume']); endif;?&gt;&lt;/td&gt;
	&lt;/tr&gt;
	&lt;tr&gt;
		&lt;th&gt;&lt;/th&gt;
