			&lt;/p&gt;
		&lt;/td&gt;
	&lt;/tr&gt;
&lt;/table&gt;
&lt;input type="hidden" name="token" value="&lt;?php echo $this-&gt;token?&gt;" /&gt;
&lt;?php if($this-&gt;tMessage and isset($this-&gt;tMessage['token'])): echo $this-&gt;tMessage['token']; endif;?&gt;

